    private void buttonhitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonhitungActionPerformed
        int panjang1, lebar1, tinggi1;
        
        panjang1 = Integer.parseInt(panjang.getText());
        lebar1 = Integer.parseInt(lebar.getText());
        tinggi1 = Integer.parseInt(tinggi.getText());
        
        int hasil1 = panjang1 * lebar1 * tinggi1;
        
        try
        {
            JOptionPane.showMessageDialog(null,"Volume Balok Adalah : " + hasil1,"luas",JOptionPane.INFORMATION_MESSAGE);
        }
        
        catch(Exception a)
        {
            JOptionPane.showMessageDialog(null,"Error : " + a,"Gagal",JOptionPane.WARNING_MESSAGE);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_buttonhitungActionPerformed

