    private void volumekubusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_volumekubusActionPerformed
        int sisi1;        
        sisi1 = Integer.parseInt(sisi.getText());
        
        int hasil = sisi1 * sisi1 * sisi1;
        
        try
        {
            JOptionPane.showMessageDialog(null, "Luas Permukaan kubus adalah : " + hasil, "luas ", JOptionPane.INFORMATION_MESSAGE);
        }
        
        catch(Exception a)
        {
            JOptionPane.showMessageDialog(null, "Error : " + a, "Gagal" , JOptionPane.WARNING_MESSAGE);
        }
        
        // TODO add your handling code here:
    }//GEN-LAST:event_volumekubusActionPerformed

    private void volumebolaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_volumebolaActionPerformed
        float jari;
        jari = Integer.parseInt(jarijari.getText());
        
        float hasil = (float) (4/3*3.14*jari*jari*jari);
        
        try
        {
            JOptionPane.showMessageDialog(null, "Luas permukaan bola adalah : " + hasil,"luas " , JOptionPane.INFORMATION_MESSAGE);
        }
        
        catch(Exception a)
        {
            JOptionPane.showMessageDialog(null, "Error : " + a,"Gagal",JOptionPane.WARNING_MESSAGE);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_volumebolaActionPerformed

